       /* Error */

/* Application data is a BIO to output status to */

EVP_PKEY_CTX_set_app_data(ctx, status_bio);

static int genpkey_cb(EVP_PKEY_CTX *ctx)
       {
       char c='*';
       BIO *b = EVP_PKEY_CTX_get_app_data(ctx);
       int p;
       p = EVP_PKEY_CTX_get_keygen_info(ctx, 0);
       if (p == 0) c='.';
       if (p == 1) c='+';
       if (p == 2) c='*';
       if (p == 3) c='\n';
       BIO_write(b,&c,1);
       (void)BIO_flush(b);
       return 1;
       }
